  `parent` varchar(512) DEFAULT NULL COMMENT '상위문서',
  `summary` varchar(1024) DEFAULT NULL COMMENT '편집요약',
  `loginedit` int(11) NOT NULL,
  PRIMARY KEY (`DocNm`,`version`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;
