--
-- Table structure for table `old_Document`
--

DROP TABLE IF EXISTS `old_Document`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `old_Document` (
  `DocNm` varchar(512) NOT NULL COMMENT '문서명',
  `content` mediumblob NOT NULL COMMENT '본문내용',
  `version` int(11) NOT NULL COMMENT '리비전',
  `strlen` bigint(20) NOT NULL COMMENT '길이',
  `type` varchar(64) CHARACTER SET utf8 NOT NULL COMMENT '관리유형',
  `category` varchar(1024) DEFAULT NULL COMMENT '분류',
  `contributor` varchar(100) NOT NULL COMMENT '기여자',
  `savetime` varchar(100) CHARACTER SET utf8 NOT NULL COMMENT '수정시각',
  `parent` varchar(512) DEFAULT NULL COMMENT '상위문서',
  `summary` varchar(1024) DEFAULT NULL COMMENT '편집요약',
  `loginedit` int(11) NOT NULL,
  `logtype` varchar(36) NOT NULL DEFAULT 'edit',
  PRIMARY KEY (`DocNm`,`version`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;
/*!40101 SET character_set_client = @saved_cs_client */;

--
-- Dumping data for table `old_Document`
--

LOCK TABLES `old_Document` WRITE;
/*!40000 ALTER TABLE `old_Document` DISABLE KEYS */;
